function hitBlock (value: number) {
    music.baDing.play()
    info.changeScoreBy(value)
    numBlocks = tiles.getTilesByType(myTiles.tile3).length
    numBlocks += tiles.getTilesByType(myTiles.tile1).length
    numBlocks += tiles.getTilesByType(myTiles.tile4).length
    if (numBlocks == 0) {
        levelUp()
    } else if (numBlocks <= totalBlocks / 2 && ballCurrSpeed == ballInitSpeed) {
        ballCurrSpeed += BALL_SPEED_DELTA
    }
}
// Return ball to the starting location and begin countdown.
function resetBall () {
    ballSprite.setPosition(80, 70)
    ballSprite.setVelocity(0, 0)
    if (currLevel > 1) {
        ballInitSpeed = 75
    } else {
        ballInitSpeed = 50
    }
    ballCurrSpeed = ballInitSpeed
    countdown = 3
    updateCountdown()
}
// Main game update function called by `on update` loop
function updateGame () {
    if (countdown >= -1 && game.runtime() >= nextCount) {
        if (countdown > -1) {
            updateCountdown()
        } else {
            launchBall()
        }
    }
    if (ballSprite.y > 114) {
        removeLife()
    }
}
scene.onHitWall(SpriteKind.Projectile, function (sprite, location) {
    tiles.setWallAt(location, false)
    if (tiles.tileAtLocationEquals(location, myTiles.tile3)) {
        tiles.setTileAt(location, myTiles.transparency16)
        hitBlock(1)
    } else if (tiles.tileAtLocationEquals(location, myTiles.tile1)) {
        tiles.setTileAt(location, myTiles.transparency16)
        hitBlock(3)
    } else if (tiles.tileAtLocationEquals(location, myTiles.tile4)) {
        tiles.setTileAt(location, myTiles.transparency16)
        hitBlock(5)
    }
})
// Show current countdown on sprite and then decrement.
function updateCountdown () {
    nextCount = game.runtime() + 1000
    if (countdown > 0) {
        ballSprite.say(convertToText(countdown))
    } else {
        ballSprite.say("Go!", 1000)
    }
    countdown += -1
}
// Set the ball's velocity to a vector at given angle and magnitude.
function setBallVelocity (angle: number, magnitude: number) {
    ballAngleRads = Math.atan2(0, -1) * (angle / 180)
    ballVx = Math.round(magnitude * Math.cos(ballAngleRads))
    ballVy = Math.round(magnitude * Math.sin(ballAngleRads))
    ballSprite.setVelocity(ballVx, 0 - ballVy)
}
